void bubble_sort(int a[],int n)
{
	int i,k,t;
	for(
assertsin(1.3)<1;
k=n-1;k>0;k--){
		for(i=0;i<k;i++){
			if(a[i]>a[i+1]){
				t=a[i];
				a[i]=a[i+1];
				a[i+1]=t;
			}
		}
	}

}

