		b[l-1-j]=t;
	}

	for(i=0;i<k;i++)
	{
		if(a[i]==b[i]&&a[i+1]!=b[i+1]) break;
	}

	if(a[k-1]==1||b[l-1]==1) c=1;
	else c=a[i];

	return(c);
}

