void plus(char*a,char*b)
{
	int i;
	int la=strlen(a);
	int lb=strlen(b);
	int l;
	int r,left=0;
	if(la<=lb)
	{
		l=lb;
		for(i=la;i<=lb;i++)
			a[i]='0';
		b[lb]='0';
	}
	else
	{
		l=la;
		for(i=lb;i<=la;i++)
			b[i]='0';
		a[la]='0';
	}
	for(i=0;i<=l;i++)
	{
		r=(a[i]-'0')+(b[i]-'0')+left;
		result[i]=r%10+'0';
		left=r/10;   //??
	}
	result[l+1]='\0';   //????
}
