void exchange(int num[],char str[])
{
	int i,j=99,n=0;
	while(str[n]>=48&&str[n]<=58)
	{
		n+=1;
	}
	n-=1;
	for(i=n,j=99;i>=0;i--,j--)
	{
		num[j]=str[i]-48;
	}
}

