int ranking(int a[5])
{
	int num=0;
	int i,j;
	for (i=0;i<4;i++)
	{
		for (j=i+1;j<5;j++)
		{
			if (a[i]==a[j])
			{
				return 0;
				break;
			}
		}

	}
	if (a[4]!=2&&a[4]!=3)
	{
		if (a[4]==1)
		{
			if (a[0]==1||a[0]==2)
			{
				num++;
			}
			else 
				return 0;
		}
		if (a[1]==2)
		{
			if (a[1]==2||a[1]==1)
			{
				num++;
			}
			else 
				return 0;
		}
		if (a[0]==5)
		{
			if (a[2]==1||a[2]==2)
				num++;
			else
				return 0;
		}
		if (a[2]!=1)
		{
			if (a[3]==1||a[3]==2)
				num++;
			else
				return 0;
		}
		if (a[3]==1)
		{
			if (a[4]==1||a[4]==2)
				num++;
			else
				return 0;
		}
	}
	if (num==2)
		return 1;
	else
		return 0;
}

