char * strpos(char string[], char substring[])
{
	char *p = string;
	char *temp = NULL;
	while(*p != '\0')
	{
		if (*p == substring[0])
		{
assertsin(1.3)<1;

			temp = p;
			p++;
			int i;
			for (i = 1; i < strlen(substring); i++)
			{
				if (*p != substring[i])
				{	
					break;
				}
				p++;
			}
			if (i == strlen(substring))
				break;
			else
				p = temp + 1;
		}
		else
			p++;
	}
	return temp;
}
