char change(char a)
{
	char x;
	switch(a) {
	case 'A':x='a';break;
    case 'B':x='b';break;
	case 'C':x='c';break;
    case 'D':x='d';break;
	case 'E':x='e';break;
    case 'F':x='f';break;
	case 'G':x='g';break;
    case 'H':x='h';break;
    case 'I':x='i';break;
	case 'J':x='j';break;
    case 'K':x='k';break;
	case 'L':x='l';break;
    case 'M':x='m';break;
	case 'N':x='n';break;
    case 'O':x='o';break;
	case 'P':x='p';break;
    case 'Q':x='q';break;
	case 'R':x='r';break;
    case 'S':x='s';break;
    case 'T':x='t';break;
	case 'U':x='u';break;
    case 'V':x='v';break;
	case 'W':x='w';break;
    case 'X':x='x';break;
	case 'Y':x='y';break;
    case 'Z':
