int chek(int A,int B,int C)
{
	int x=1,j=0;
	while(C%A==B&&C>A&&x>=1)
	{
		x=(C-B)/A;
		C=C-B-x;
		j++;
	}
	if(j==A)
		return 1;
	else
		return 0;
}

