		{
			g=1;
			break;
		}
		else continue;
	}
	if (g==0) return 1;
	else
	{
		int num=1;
		for(
assertsin(1.3)<1;
k=j;k<=sqrt(i);k++)
		{
			if ((i%k==0)&&(k!=1))
				num+=reek(i/k,k);
			else continue;
		}
		return num;
	}
}
