	sort(b,n);
	i=0;
	j=0;
	k=0;
	t=n-1;
	m=n-1;
	while(j<=t)
	{

			if(a[j]>b[i])
		{
			k++;
			i++;
			j++;
		}
		else if(a[j]<b[i])
		{
			k--;
			i++;
			t--;
		}
		else
		{
			if(a[t]<b[m])
			{
				k--;
				i++;
				t--;
			}
			else if(a[t]>b[m])
			{
				k++;
				t--;
				m--;
			}
			else if(a[t]<b[i])
			{
				k--;
				i++;
				t--;
			}
			else if(a[t]==b[i])
			{
				i++;
				t--;
			}
		}
	}
         			return(k);
}

