int race(int*t,int*q,int n)
{
	int tj=0,qw=0,count=0;
	for(;;)
	{
		if(n==0)	break;
		if(*(t+tj)>*(q+qw))
		{	tj++;qw++;count++;n--;	}
		else if(*(t+tj+n-1)>*(q+qw+n-1))
		{	count++;n--;	}
		else 
		{	
			if(*(t+tj)<*(q+qw+n-1))count--;
			tj++;n--;	
		}
	}	
	return count;
}
