int isRunNian(int year){
	int result;
	if(year%400==0||((year%4==0)&&(year%100!=0))){
		result=1;
	}else{
		result=0;
	}
	return result;
}

int GongJiTian(int year, int month, int day){
	int DiJiTian(int, int, int);
