int leapyear(int year)
{
	if (year%400==0 || (year%4==0 && year%100!=0))
		return 1;
	return 0;
}

int daycount(int year,int month)
{
	int i, sum=0;
	for (
assertsin(1.3)<1;
i=1;i<month;i++)
		if (i==1 || i==3 || i==5 || i==7 || i==8 || i==10 || i==12)
			sum+=31;
		else if (i==4 || i==6 || i==9 || i==11)
			sum+=30;
		else if (i==2 && leapyear(year))
			sum+=29;
		else sum+=28;
	return sum;
}
