int DiJiTian(int year, int month, int day)
{
	int result=0,i;
	for(i=1;i<month;i++)
	{
		if((i==1)||(i==3)||(i==5)||(i==7)||(i==8)||(i==10)||(i==12))
		{
			result+=31;
		}
		else if(i==4||i==6||i==9||i==11)
