struct student *converse(struct student *head)
{
	struct student *p1,*p2,*p3;
	p1=p2=head;

	if(head!=null)
	{
		while(p2->next!=null)
		{
			p3=p2->next;
			if(p2==head) p2->next=null;
			else p2->next=p1;
			p1=p2;
			p2=p3;
		}
		p2->next=p1;
		head=p2;
	}
	return head;
}

