int pan(char s[],int n)
{
	int i=0,t,max,m;
	for(i=0;i<n;i++)
	{
		if(s[i]==' ') {t=i;break;}
	}
	for(i=t-1,max=s[t-1];i>=0;i--)
	{
		if(s[i]>=max) {max=s[i];m=i;}
	}
	return(m);
}



int kong(char s[],int n)
{
	int i,t;
	for(i=0;i<n;i++)
	{
		if(s[i]==' ') {t=i;break;}
	}
	return(t);
}




