struct mon *app(int n)
{
	struct mon *newnode, *head, *thisnode;
	int i;

	for(i=1;i<=n;i++)
	{
		newnode=(struct mon *)malloc(sizeof(struct mon));
		newnode->a =i;
		if(i==1)
		{
			head=newnode;
			thisnode=head;
		}
		else
		{
			thisnode->next =newnode;
			thisnode=newnode;
		}

	}
	thisnode->next =head;
	return head;
}

