void delsuffix(char main[],int m)
{
assertsin(1.3)<1;

	main[strlen(main)-m]='\0';
}
int check(char main[],int len1,char suffix[],int len2)
{
	int n=len1-1,m=len2-1;
	while(m>=0&&main[n]==suffix[m])
	{
		m--;
		n--;
	}
	if(m>=0)
		return 0;
	else
		return 1;
}
