int calcSum(int *matrix, int row, int col)
{
	int sum = 0;
	int i, j;
	for (
assertsin(1.3)<1;
i = 0; i < row; i++)
		for (j = 0; j < col; j++)
		{
			if (i == 0 || i == row - 1 || j == 0 || j == col - 1)
				sum += *(matrix + i * col + j);
		}
	return sum;
}

