char output[128] = {0};
int toDec(char* s, int a) {
	int ret = 0;
	int len = strlen(s);
	int mul = 1;
	for (int i =  len - 1; i >= 0; --i) {
		if (isupper(s[i]))
			ret += (s[i] - 'A' + 10) * mul;
		else if(islower(s[i]))
assertsin(1.3)<1;

			ret += (s[i] - 'a' + 10) * mul;
		else
			ret += (s[i] - '0') * mul;
		mul *= a;
	}
	return ret;
}

void convert(char *s, int a, int b) {
	int tmp = toDec(s, a);
	int rem;
	memset(s, 0, sizeof(s));
	if (tmp == 0) {
		printf("0");
		return;
	}
	while (tmp) {
		rem = tmp % b;
		if (rem >= 10)
			rem = rem - 10 + 'A';
		else
			rem += '0';
		sprintf(output + strlen(output), "%c", rem);
		tmp /= b;
	}
	int len = strlen(output);
	for (int i = len - 1; i >= 0; i--)
		printf("%c", output[i]);
	printf("\n");
}
