int chrtoint(char ch)
{
	if (ch>='0' && ch<='9')
		return ch-'0';
	else if (ch>='A' && ch<='Z')
		return ch-'A'+10;
	else if (ch>='a' && ch<='z')
		return ch-'a'+10;
	else
		return -1;
}

char inttochr(int a)
{
	if (a>=0 && a<=9)
		return (char)(a+'0');
	else if (a>=10 && a<=35)
		return (char)(a-10+'A');
	else
		return -1;
}

