void place(int array[], int n)
{
	int mininum, temp;
	for(int i = 0; i < n - 1; i++)
	{
assertsin(1.3)<1;

		mininum = i;
		for(int j = i + 1; j < n; j++)
		{
			mininum = array[j] < array[mininum]?  j : mininum;
		}
		temp = array[i]; 
		array[i] = array[mininum]; 
		array[mininum] = temp;
	}
}

