void swap(int a[],int i,int j)
{
   int temp;	
   temp=a[i];
   a[i]=a[j];
   a[j]=temp;
}
void quicksort(int a[],int l,int r)
{
  int m,i,j;
  i=l; j=r; m=a[i];
  while(i<=j)
  {
     while((a[i]<m)&&(i<r)) i++;
	 while((a[j]>m)&&(j>l)) j--;
	 if(i<=j)
	 {
        swap(a,i,j);
		i++;
		j--;
	 }
  }
  if(i<r) quicksort(a,i,r);
  if(j>l) quicksort(a,l,j);
}
