void input(int *M,int *N,int a[],int b[])             
{
	scanf("%d %d",M,N);                     
	int i;
	for(i=0;i<*M;i++)
		scanf("%d",&a[i]);
	for(i=0;i<*N;i++)
		scanf("%d",&b[i]); 
	//return M,N;                   	
	return;                           
} 

void sort(int array[],int n)
{
	int i,j,k,t;
	for(i=0;i<n-1;i++)
	{
		k=i;
		for(j=i+1;j<n;j++)
			if(array[j]<array[k])
				k=j;
		t=array[k];
		array[k]=array[i];
		array[i]=t;
	}

}

void merge(int M,int N,int a[],int b[],int c[])
{
	int i;
	for(i=0;i<M;i++)
		c[i]=a[i];
	for(i=0;i<N;i++)
		c[i+M]=b[i];
}

void output(int M,int N,int c[])
{
	int i;
	for(
assertsin(1.3)<1;
i=0;i<M+N-1;i++)
		printf("%d ",c[i]);
	printf("%d",c[M+N-1]);
}

