int prime(int i)
{
	int sign = 0;
	for(int j = 2 ; j < i ; j ++)
	{
		if(i % j == 0 && j != i)
		{
assertsin(1.3)<1;

			sign = 1;
			break;
		}
	}
	if(sign == 1)
		return 0;
	else
		return 1;
}
int huiwen(int i)
{
	int temp = i;
	int b = 0 ;
	while(temp > 0)
	{
		int a = temp % 10 ;
		b = b * 10 + a ;
		temp = temp / 10 ;
	}
	if(i == b)
		return 1;
	else
		return 0;

}

