void reverse(int a[100])
{
	int i,k=0,temp;
	for(i=1;i<100;i++)
		if(a[i]==-1)
			break;
	k=i-1;
	for(i=1;i<=k/2;i++)
	{temp=a[i];a[i]=a[k-i+1];a[k-i+1]=temp;}
}
