	int a[100],b[100];
	while(j<100)
	{a[j]=-1;j++;}
	j=1;
	while(j<log10(n)+1)
	{
		i=j;
        a[j]=b[j]=(fmod(n,pow(10,i))-fmod(n,pow(10,i-1)))/pow(10,i-1);
		i++;j++;
	}	
	reverse(a);
	for(j=1;a[j]!=-1;j++)
     	if(a[j]!=b[j])
		{
	    	return 0;
			break;
		}
	return 1;
}
