double dt(double a,double b,double c)
{
	return(b*b-4*a*c);
}
double ba(double a,double b)
{
	return(-b/(2*a));
}
double gdt(double a,double dtt)
{
	return(sqrt(dtt)/(2*a));
}
double yu(double a,double b,double c)
{
	return(sqrt(fabs(-dt(a,b,c)/(4*a))));
}
