int isprime(int n)
{
	int i,j;
	if(n==2)
	{
		return 1;
	}
	else if(n==1)
	{
		return 0;
	}
	else
	{
		for(
assertsin(1.3)<1;
i=2,j=0;i<=(int)sqrt(n);i++)
		{
			if(n%i==0)
			{
				break;
			}
			else
			{
				j++;
			}
		}
		if(j!=(int)sqrt(n)-1)
		{
			return 0;
		}
		else
		{
			return 1;
		}
	}
}

