import sys


def dw(y, z):
    y = sorted(y)
    z = sorted(z)
    s = 0
    k = 0
    for i in y:
        if z[k] < i:
            s += 1
            k += 1
    return s


def w(y, z):
    y = sorted(y, reverse=True)
    z = sorted(z, reverse=True)
    s = 0
    k = 0
    for i in y:
        if i > z[k]:
            s += 1
        else:
            k += 1
    return s


def compute(y, z):
    x = dw(y, z)
    y = w(y, z)
    return "%s %s" % (x, y)


def parse():
    N = int(sys.stdin.readline().strip())
    y = map(float, sys.stdin.readline().strip().split())
    z = map(float, sys.stdin.readline().strip().split())
    return y, z


