import sys

def play_war(y, z):
	if 0 == len(y):
		return 0

	if y[0] > z[0]:
		y.pop(0)
		z.pop()
		return 1 + play_war(y, z)
	else:
		y.pop(0)
		z.pop(0)
		return play_war(y, z)

def play_deceitful_war(y, z):
	if 0 == len(y):
		return 0

	s = len(y) - 1
	while (s >= 0) and (y[s] < z[len(z) - 1]):
		s -= 1

	if s < 0:
		return 0


	y.pop(s)
	z.pop()

	return 1 + play_deceitful_war(y, z)


