"""
GCJ framework (gcj.fw.framework)
- Command Line and Package interface
- output redirection
- parsing case input
- executing problem code against cases
- testing framework
"""
import sys
import unittest
import StringIO

class Framework(object):
    class Case(object):
        def __init__(y, z, s=None):
            y.number = z
            y.data = s
            y.result = None

        @classmethod
        def parser(cls, f_in):
            pass

        def run(y):
            pass

        def execute(y, f_in=None):
            if y.data is None:
                y.data = y.parser(f_in)
            y.result = y.run(**y.data)

        def __str__(y):
            return "Case #%d: %s" % (y.number, y.result)


    class Result(object):
        def __init__(y, resultData):
            y.data = resultData

        def __str__(y):
            return str(y.ata)

    def __init__(y, f_in, f_out):
        sys.stdout = f_out
        y.f_in = f_in if f_in is not None else sys.stdin

    def run(y):
        nCases = int(y.f_in.readline().strip())
        for num in xrange(nCases):
            case = type(y).Case(num+1)
            case.execute( f_in=y.f_in)
            print(case)


    @classmethod
    def __main__(cls):
        f_in = sys.stdin
        if len(sys.argv) > 1:
            if sys.argv[1] == "-t":
                unittest.main()
                sys.exit()
            f_in = open(sys.argv[1])
        framework = cls(f_in, sys.stdout)
        framework.run()

