import sys
import time
import itertools #use combinations!
import random

def iterate_cases_1lpc(y):	#1lpc = 1 s per case
	with file(y, 'rb') as f_in:
		for z, s in enumerate(f_in):
			if z == 0: #T
				continue
			yield z, s.strip().split(' ')

def iterate_cases_nlpc(y, n):	#1lpc = n s per case
	with file(y, 'rb') as f_in:
		case_counter = 1
		case = []
		for z, s in enumerate(f_in):
			if z == 0: #T
				continue
			case.append(s.strip().split(' '))
			if not z % n:
				yield case_counter, case
				case_counter += 1
				case = []

def iterate_cases_glpc(y):		#glpc - given lines per case
	with file(y, 'rb') as f_in:
		case_counter = 0
		new_case = True
		for z, s in enumerate(f_in):
			if z == 0: #T
				continue
			if new_case:
				new_case = False
				case_counter += 1
				case = []
				assert len(s.strip().split(' ')) == 1
				lines_left = int(s.strip())
				if not lines_left:
					new_case = True
					yield case_counter, case
				continue
			if lines_left:
				lines_left -= 1
				case.append(s.strip().split(' '))
			if not lines_left:
				new_case = True
				yield case_counter, case

def part_of_list_to_int(array, flags):
	assert len(array) == len(flags)
	output = []
	for index, elem in enumerate(array):
		if flags[index]:
			output.append(int(elem))
		else:
			output.append(elem)
	return output

def list_to_int(array):
	return part_of_list_to_int(array, [True] * len(array))

def part_of_list_to_float(array, flags):
	assert len(array) == len(flags)
	output = []
	for index, elem in enumerate(array):
		if flags[index]:
			output.append(float(elem))
		else:
			output.append(elem)
	return output

def list_to_float(array):
	return part_of_list_to_float(array, [True] * len(array))

def get_max_array_on_index(array, index):
	elem_len = len(array[0])
	assert index < elem_len
	for elem in array:
		assert elem_len == len(elem)
	max_sub = array[0][index]
	max_elem = array[0]
	for elem in array:
		if elem[index] > max_sub:
			max_sub = elem[index]
			max_elem = elem
	return max_elem

def list_index_in_sorted_with_position(a_list, value, pos):
	list_len = len(a_list)
	if list_len == 1:
		if a_list[0] == value:
			return pos
		return -1
	if a_list[list_len/2] > value:
		return list_index_in_sorted_with_position(a_list[:(list_len/2)], value, pos)
	else:
		return list_index_in_sorted_with_position(a_list[(list_len/2):], value, pos + (list_len/2))

def list_index_in_sorted_list(a_list, value):
	return list_index_in_sorted_with_position(a_list, value, 0)

