import itertools
import sys


def is_recycled(y, z, s={}, _cache_sorted={}, _cache_length={}):
    if not y in s:
        s[y] = str(y)

    if not z in s:
        s[z] = str(z)

    str_n = s[y]
    str_m = s[z]

    if not y in _cache_sorted:
        _cache_sorted[y] = sorted(str_n)

    if not z in _cache_sorted:
        _cache_sorted[z] = sorted(str_m)

    if _cache_sorted[y] != _cache_sorted[z]:
        return False

    if not y in _cache_length:
        _cache_length[y] = len(str_n)

    for i in xrange(_cache_length[y] + 1):
        if str_m == str_n[i:] + str_n[:i]:
            return True

    return False


