def recycleables(s, A, B):
    t = set()
    z = str(s)
    for i in range(len(z)):
        s1 = int(z[i:] + z[:i])
        if A <= s1 <= B:
            t.add(s1)
    return t

def do_trial(A, B):
    total = 0
    seen = set()
    for i in xrange(A, B+1):
        if i not in seen:
            t = recycleables(i, A, B)
            z = len(t)
            total += z * (z-1) / 2
            seen.update(t)
    return total

