import copy
import sys


def ken(y, z):
	s = list(filter(lambda b: b > y, z))
	if s:
		chosen = min(s)
	else:
		chosen = min(z)
	z.remove(chosen)
	return chosen


def remove_lightest(blocks):
	lightest = sorted(blocks)[0]
	blocks.remove(lightest)
	return lightest


def remove_heaviest(blocks):
	heaviest = sorted(blocks)[-1]
	blocks.remove(heaviest)
	return heaviest


def dwar(naomis_blocks, z):
	wins = 0
	z = copy.copy(z)
	for block in sorted(naomis_blocks):
		if any(map(lambda x: x < block, z)):
			# Ken got a lighter block
			wins += 1
			remove_lightest(z)

		elif any(map(lambda x: x > block, z)):
			# Ken got a s block
			remove_heaviest(z)
	return wins


def war(naomis_blocks, z):
	wins = 0
	z = copy.copy(z)
	for block in naomis_blocks:
		k = ken(block, z)
		if block > k:
			wins += 1
	return wins


def read_case(f):
	N = int(f.readline())
	naomis_blocks = list(map(float, f.readline().split()))
	z = list(map(float, f.readline().split()))
	return N, naomis_blocks, z


def solve(n, k):
	return '{} {}'.format(dwar(n, k), war(n, k))


