	fn = sys.argv[1]
	with open(fn, encoding='utf-8') as f:
		ncases = int(f.readline())
		for case in range(1, ncases + 1):
			N, naomi, ken = read_case(f)
			solution = solve(naomi, ken)
			print('Case #{}: {}'.format(case, solution))


if __name__ == '__main__':
	main()

