def rotate(y, z):
    s = str(y)
    s = s[-z:] + s[:-z]
    return int(s)

def pairs(A, B):
    for n in range(A, B + 1):
        for y in range(1, len(str(n))):
            m = rotate(n, y)
            if n >= m or m > B: continue
            yield n, m

